import React from "react";
import { Link } from "react-router-dom";

const Card = ({ name, username, id }) => {

  const addFav = ()=>{
    const existingData = localStorage.getItem("dentistasFav");

    let favArray = [];

    if (existingData) {
      try {
        favArray = JSON.parse(existingData);
        if (!Array.isArray(favArray)) {
          throw new Error("Los datos existentes no son un array válido.");
        }
      } catch (error) {
        console.error("Error al parsear los datos existentes del localStorage:", error);
        return;
      }
    }

    const exists = favArray.some((item) => item.id === contextValue.data[id - 1].id);
    if (exists) {
      return;
    }

    favArray.push(contextValue.data[id - 1]);

    localStorage.setItem("apiData", JSON.stringify(favArray));
  }

  return (
    <div className="card">
        <p>{id}</p>
        <p>{username}</p>
        <img src="/images/doctor.jpg" alt="doctor placeholder" />
        
        <Link to={`/detail/${id}`}>
        {name}
        </Link>

        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
